import React, { Component } from "react";
import './counter.css';
import './counter';

export class features extends Component {
  counter() {
    const counters = document.querySelectorAll('.counter');
const speed = 200; // The lower the slower

counters.forEach(counter => {
	const updateCount = () => {
		const target = +counter.getAttribute('data-target');
		const count = +counter.innerText;

		// Lower inc to slow and higher to slow
		const inc = target / speed;

		// console.log(inc);
		// console.log(count);

		// Check if target is reached
		if (count < target) {
			// Add inc to count and output in counter
			counter.innerText = count + inc;
			// Call function every ms
			setTimeout(updateCount, 1);
		} else {
			counter.innerText = target;
		}
	};

	updateCount();
});
  }
  render() {
    return (
      <div id="features" className="text-center">
        <div className="container">
          <div className="col-md-10 col-md-offset-1 section-title">
            <h2>Features</h2>
          </div>
          {/* <div className="row">
            {this.props.data
              ? this.props.data.map((d,i) => (
                  <div  key={`${d.title}-${i}`} className="col-xs-6 col-md-3">
                    {" "}
                    <i className={d.icon}></i>
                    <h3>{d.title}</h3>
                    <p>{d.text}</p>
                  </div>
                ))
              : "Loading..."}
          </div> */}

<section className="counters">
        <div className="container">
          <div>
            <i className="fab fa-youtube fa-4x" />
            <div className="counter" data-target={60000}>0</div>
            <h3>Subscribers</h3>
          </div>
          <div>
            <i className="fab fa-twitter fa-4x" />
            <div className="counter" data-target={15000}>0</div>
            <h3>Followers</h3>
          </div>
          <div>
            <i className="fab fa-facebook fa-4x" />
            <div className="counter" data-target={9000}>0</div>
            <h3>Likes</h3>
          </div>
          <div>
            <i className="fab fa-linkedin fa-4x" />
            <div className="counter" data-target={5000}>0</div>
            <h3>Connections</h3>
          </div>
        </div>
      </section>
        </div>
      </div>
    );
  }
}

export default features;
